CREATE TABLE IF NOT EXISTS `yx_members` (  `id` int(20) NOT NULL auto_increment,  `groupid` int(3) NOT NULL,  `account` varchar(30) NOT NULL,  `password` varchar(60) NOT NULL,  `rmb` int(8) NOT NULL default '0',  `crmb` int(8) NOT NULL default '0',  `nickname` varchar(30) NOT NULL,  `email` varchar(30) NOT NULL,  `tel` varchar(15) NOT NULL,  `qq` varchar(20) NOT NULL,  `regtime` int(11) NOT NULL,  `regip` varchar(16) NOT NULL,  `lasttime` int(11) NOT NULL,  `lastip` varchar(16) NOT NULL,  `islock` tinyint(4) NOT NULL,  PRIMARY KEY  (`id`)) ENGINE=MyISAM AUTO_INCREMENT=2 DEFAULT CHARSET=utf8;
