CREATE TABLE IF NOT EXISTS `yx_member_group` (  `id` int(3) NOT NULL auto_increment,  `name` varchar(30) NOT NULL,  `notallow` text NOT NULL,  PRIMARY KEY  (`id`)) ENGINE=MyISAM AUTO_INCREMENT=4 DEFAULT CHARSET=utf8;
INSERT INTO `yx_member_group`(`id`,`name`,`notallow`) VALUES ('1','未登录','member/index/index|member/infor|member/order');
INSERT INTO `yx_member_group`(`id`,`name`,`notallow`) VALUES ('2','普通会员','');
CREATE TABLE IF NOT EXISTS `yx_orders` (  `id` int(15) NOT NULL auto_increment,  `ordernum` varchar(20) NOT NULL COMMENT ' 订单号',  `account` varchar(30) NOT NULL COMMENT '账户',  `total` float NOT NULL COMMENT '总价',  `freight` float NOT NULL COMMENT '运费',  `ordertime` int(11) NOT NULL COMMENT '订单时间',  `state` tinyint(1) NOT NULL COMMENT '订单状态',  `mess` text NOT NULL COMMENT '订单信息',  PRIMARY KEY  (`id`)) ENGINE=MyISAM DEFAULT CHARSET=utf8;
CREATE TABLE IF NOT EXISTS `yx_order_detail` (  `id` int(20) NOT NULL auto_increment,  `code` varchar(10) NOT NULL COMMENT '商品编号',  `ordernum` varchar(20) NOT NULL,  `name` varchar(100) NOT NULL,  `price` float NOT NULL,  `num` int(5) NOT NULL,  PRIMARY KEY  (`id`)) ENGINE=MyISAM DEFAULT CHARSET=utf8;
